import React, { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import data from "./data";

const Home = () => {
	const [state, setState] = useState(data);

	const handleDragDrop = (result) => {
		if (!result.destination) return;
		const items = Array.from(state);
		const [reorderedItem] = items.splice(result.source.index, 1);
		console.log(reorderedItem, "reorderedItem");
		items.slice(result.destination.index, reorderedItem.index);
		setState(items);
	};

	return (
		<div>
			<p className="text-3xl text-center">Drag drop example</p>
			<hr className="w-screen" />
			<br />
			<DragDropContext onDragEnd={handleDragDrop}>
				<Droppable droppableId="dropableComponent">
					{(provided) => (
						<div
							{...provided.droppableProps}
							ref={provided.innerRef}
							className="w-3/5 mx-auto"
						>
							{data.map((item, index) => (
								<Draggable key={item.id} index={index} draggableId={item.id}>
									{(provided) => (
										<div
											{...provided.draggableProps}
											{...provided.dragHandleProps}
											ref={provided.innerRef}
											className="p-4 w-3/5 mx-auto bg-gray-200 rounded-xl hover:bg-gray-300 my-2"
										>
											<p>{item.id}</p>
										</div>
									)}
								</Draggable>
							))}
							{provided.placeholder}
						</div>
					)}
				</Droppable>
			</DragDropContext>
		</div>
	);
};
export default Home;
