import { ToastContainer, toast } from "react-toastify";

const Home = () => {

	const connectWallet = async() => {
		if (window.ethereum === undefined) {
			toast.error("Metamask is not installed, please install metamask");
		} else {
			const accounts = await window.ethereum({ method: "eth_requestAccounts" });
			console.log(window.ethereum, "")
			toast.success("User connected metamask successfully");
			console.log(accounts, "accounts");
		}
	};

	return (
		<div>
			<button
				onClick={connectWallet}
				className="p-2 m-2 border border-gray-700 hover:bg-indigo-800 hover:text-white rounded-md"
			>
				Connect Wallet
			</button>
			<ToastContainer />
		</div>
	);
};
export default Home;
